import { StyleSheet } from 'react-native'

export default StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff'
  },
  logo: {
    width: 150,
    height: 80,
    margin: 20
  },
  row: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom:20,
    padding:10
  },
  halfRow: {
    flex: 0.5,
    justifyContent: 'center'
  },
  h4Text: {
    textAlign: 'right',
    paddingRight: 20
  },
  event: {
    width: 140,
    height: 100,
    margin: 15
  },
  winery: {
    width: 60,
    height: 60,
    margin: 15
  },
  wine: {
    width: 80,
    height: 80,
    margin: 15
  }


});
