import React, { useState, useEffect, } from 'react';
import { View, Image, Text, ActivityIndicator, ScrollView } from 'react-native';
import styleSheet from '../Pages/PageStyle'

function FCEvents() {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        getEvents();
    }, []);

    function getEvents() {
        fetch('http://proj.ruppin.ac.il/bgroup15/prod/api/Event',
            {
                method: 'GET',
                headers: new Headers({
                    'Content-Type': 'application/json; charset=UTF-8',
                    'Accept': 'application/json; charset=UTF-8',
                })
            })
            .then(res => {
                <ActivityIndicator size="large" />
                return res.json();
            })
            .then(
                (result) => {
                    setEvents(result);
                },
                (error) => {
                    console.log("err post=", error);
                });
    }

    return (
        <ScrollView
            horizontal={true}
            pagingEnabled={true}
            style={{
                alignContent: 'center',
                borderBottomColor: 'black'
            }}>
            {events.map(item => (
                <View style={styleSheet.row} key={item.ID}>
                    <Image
                        source={{ uri: item.Image }}
                        style={styleSheet.event} />
                    <View style={{ alignItems: 'center' }}>
                        <Text>{item.Name}</Text>
                        <Text>{item.Date}</Text>
                        <Text>{item.date}</Text>
                    </View>
                </View>

            ))}
        </ScrollView>
    )
}
export default FCEvents;
