import React, { Component } from 'react'
import axios from 'axios';
import { ScrollView, View, Text } from 'react-native';
import styles from '../Pages/PageStyle'

export default class CCHomePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            wineries: [],
            apiUrl: 'http://proj.ruppin.ac.il/bgroup15/prod/api/'
        }
    }


    componentDidMount() {
        fetch(this.state.apiUrl + 'Winery',
            {
                method: 'GET',
                headers: new Headers({
                    'Content-Type': 'application/json; charset=UTF-8',
                    'Accept': 'application/json; charset=UTF-8',
                })
            })
            .then(res => {
                return res.json();
            })
            .then(
                (result) => {
                    this.setState({
                        wineries: result
                    })
                },
                (error) => {
                    console.log("err post=", error);
                });
    }


    render() {
        return (
            <ScrollView
                horizontal={true}
                pagingEnabled={true}
                style={styles.scroll}
            >
                {this.state.wineries.map(item => (
                    <View key={item.ID} style={styles.container}>
                        <Text>{item.WineryName}</Text>
                        <View>
                            <Image
                                source={{ uri: item.WineryImage }}
                                style={{ width: 60, height: 60, margin: 20 }} />
                        </View>
                    </View>

                ))}
            </ScrollView>
        )
    }
}
