import React, { Component } from 'react'
import axios from 'axios';
import {ScrollView, View, Text} from 'react-native';
import styles from '../Pages/PageStyle'

export default class CCHomePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            wineries: []
        }
    }
    componentDidMount = async () => {
        let data = await axios(
            'http://proj.ruppin.ac.il/bgroup15/prod/api/Winery',
        );
        
        this.setState({
            wineries : data
        })
    }

    render() {
        return (
            <ScrollView
                horizontal={true}
                pagingEnabled={true}
                style={styles.scroll}
            >
                {this.state.wineries.map(item => (
                    <View key={item.ID} style={styles.container}>
                        <Text>{item.WineryName}</Text>
                        <View>
                            <Image
                                source={{ uri: item.WineryImage }}
                                style={{ width: 60, height: 60, margin: 20 }} />
                        </View>
                    </View>

                ))}
            </ScrollView>
        )
    }
}
