import * as React from 'react';
import * as WebBrowser from 'expo-web-browser';
import * as Facebook from 'expo-auth-session/providers/facebook';
import { ResponseType } from 'expo-auth-session';
import { Button } from 'react-native';
import { Alert } from 'react-native';

WebBrowser.maybeCompleteAuthSession();

export default function FCFacebookLogin() {
  const [request, response, promptAsync] = Facebook.useAuthRequest({
    clientId: '174194160927637',
    responseType: ResponseType.Code,
    });

  React.useEffect(() => {
    if (response?.type === 'success') {
      const { code } = response.params;
      Alert.alert(response.params);
      }
  }, [response]);

  async function LoginTofacebook() {
    const [picture, setPicture] = useState([]);
    try{
        await Facebook.initializeAsync(['174194160927637']);
        const{type, token,expires,permissions,declinedPermissions}=
        await Facebook.logInWithReadPermissionsAsync({
            permissions:['public_Profile']
        });
        if(type ==='success'){
            const response=await fetch('https://graph.facebook.com/me?fields=id,name,email,picture&access_token=${token}');
            let res=await response.json();
            Alert.alert('Logged in!!!', `Name:${res.name}\nEmail:${res.email}\nPicture:${res.picture.data.url}`);
        }
        else{type==='cancel'}
    }
    catch ({message}){
            Alert.alert(`Facebook login Failed ${message}`);
    }

  return (
    <Button
      disabled={!request}
      title="Countinue with Facebook"
      onPress={() => {
        LoginTofacebook();
        }}
    />
  );
    }
}
