import React, { useState, useEffect, } from 'react';
import {  View, Image,Text,  ActivityIndicator, ScrollView } from 'react-native';
import styleSheet from '../Pages/PageStyle'

function FCWines() {
  const [wines, setWines] = useState([]);

  useEffect(() => {
    getWines();
  }, []);

  function getWines() {
    fetch('http://proj.ruppin.ac.il/bgroup15/prod/api/Wine',
      {
        method: 'GET',
        headers: new Headers({
          'Content-Type': 'application/json; charset=UTF-8',
          'Accept': 'application/json; charset=UTF-8',
        })
      })
      .then(res => {
        <ActivityIndicator size="large" />
        return res.json();
      })
      .then(
        (result) => {
          setWines(result);
        },
        (error) => {
          console.log("err post=", error);
        });
  }

  return (
    <ScrollView
      horizontal={true}
      pagingEnabled={true}
      style={{
        alignContent: 'center',
        borderBottomColor:'black'
      }}>
      {wines.map(item => (
        <View key={item.ID}>
          <Image
            source={{ uri: item.WineImg }}
            style={styleSheet.wine} />
          <Text>{item.WineName}</Text>
          <View>
          </View>
        </View>

      ))}
    </ScrollView>

  )
}
export default FCWines;
