import React, { useState, useEffect, } from 'react';
import {Text,View, Image} from 'react-native';
import axios from 'axios';

function App() {
  const [data, setData] = useState( [] );

  useEffect(async () => {
    const result = await axios(
      'http://proj.ruppin.ac.il/bgroup15/prod/api/Winery',
    );
    setData(result.data);
  }, []);

  return (
    <View>
      {data.map(item => (
        <View key={item.ID}>
          <Text>{item.WineryName}</Text>
          <View>
            <Image
            source={item.WineryImage}/>
          </View>
        </View>

      ))}
    </View>
  );
}

export default App;
